import React, {Component} from 'react';

class Count extends Component {
    state={
        count:0
    }
    render() {
        return (
            <div>
                <h1>当前的和为:{this.state.count}</h1>
                <select>
                    <option value='1'>1</option>
                    <option value='2'>2</option>
                    <option value='3'>3</option>
                </select>&nbsp;&nbsp;
                <button>+</button>&nbsp;
                <button>-</button>&nbsp;
                <button>*</button>&nbsp;
                <button>/</button>&nbsp;
            </div>
        );
    }
}

export default Count;
