import React, {Component} from 'react';
import './index.less'
class Count extends Component {
    //åŠ 
    addhandle= ()=>{
        const{value} = this.selectNumber;
        this.props.addnumber(value*1);
    }
    dechandle=()=>{
        const{value} = this.selectNumber;
        this.props.dec(value*1);
    }
    multihandle=()=>{
        const{value}=this.selectNumber;
        this.props.multi(value*1);
    }
    divihandle=()=>{
        const{value} = this.selectNumber;
        this.props.divi(value*1);
    }

    render() {
        return (
            <div className="Count">
                <div className="Count_head">
                    <h1>{this.props.count}</h1>
                </div>
                <div className="Count_main">
                    <select className="temp" ref={(c)=>{this.selectNumber=c}}>
                        <option value='1'>1</option>
                        <option value='2'>2</option>
                        <option value='3'>3</option>
                    </select>
                    <button className="temp" onClick={this.addhandle}>+</button>
                    <button className="temp" onClick={this.dechandle}>-</button>
                    <button className="temp" onClick={this.multihandle}>*</button>
                    <button className="temp" onClick={this.divihandle}>/</button>
                    <button className="temp">#</button>
                    <button className="temp">#</button>
                    <button className="temp">#</button>
                    <button className="temp">#</button>
                    <button className="temp">#</button>
                    <button className="temp">#</button>
                    <button className="temp">#</button>
                    <button className="temp">#</button>
                    <button className="temp">#</button>
                    <button className="temp">0</button>
                </div>


            </div>
        );
    }
}

export default Count;
