import React, {Component} from 'react';
import './index.less'
const temp = [];
let temp2 = '';
const temp3 = [];
class Count extends Component {
    //加
    addhandle= ()=>{
       const value = this.state.input_number;
        this.props.addnumber(value*1);
    }
    dechandle=()=>{
        const{value} = this.selectNumber;
        this.props.dec(value*1);
    }
    multihandle=()=>{
        const{value}=this.selectNumber;
        this.props.multi(value*1);
    }
    divihandle=()=>{
        const{value} = this.selectNumber;
        this.props.divi(value*1);
    }
    getresult=()=>{
        if(this.state.do==='+'){
            const sum=this.state.input_number+this.state.input_number2;
            this.setState({input_number:sum,input_number2:'0',do:''});
        }
    }
    handleInput=(value)=> {
        return ()=>{
            if(value==='+'){
                this.state.do = value;
              this.setState({do:value})
            }
            else if(this.state.do!=='+'){
                this.state.input_number.push(value);
                console.log(this.state.input_number);
                let temps='';
                for(let i=0;i<this.state.input_number.length;i++){
                    temps = temps+this.state.input_number[i]
                }
                temps = parseInt(temps);
                this.setState({input_number:temps});
            }else{
                this.state.input_number2.push(value);
                console.log('temp3',this.state.input_number2);
                let temps = '';
                for(let i=0;i<this.state.input_number2.length;i++){
                    temps = temps+this.state.input_number2[i]
                }
                temps = parseInt(temps);
                this.setState({input_number2:temps});
            }

        }
    }
    state={
        //记录你点击的数字的
        input_number:'0',
        input_number2:'0',
        do:''
    }
    render() {
        return (
            <div className="Count">
                <div className="Count_head">
                    <h1>{this.state.input_number} &nbsp;&nbsp;{this.state.do}&nbsp;&nbsp;{this.state.input_number2}</h1>
                </div>
                <div className="Count_main">
                    <div className="Count_do">
                        <button className="temp" onClick={this.handleInput('+')}>+</button>
                        <button className="temp" onClick={this.handleInput('-')}>-</button>
                        <button className="temp" onClick={this.handleInput('*')}>*</button>
                        <button className="temp" onClick={this.handleInput('/')}>/</button>
                    </div>
                    <button className="temp"  onClick={this.handleInput('7')}>7</button>
                    <button className="temp"  onClick={this.handleInput('8')}>8</button>
                    <button className="temp"  onClick={this.handleInput('9')}>9</button>
                    <button className="temp"  onClick={this.handleInput('4')}>4</button>
                    <button className="temp"  onClick={this.handleInput('5')}>5</button>
                    <button className="temp"  onClick={this.handleInput('6')}>6</button>
                    <button className="temp"  onClick={this.handleInput('1')}>1</button>
                    <button className="temp"  onClick={this.handleInput('2')}>2</button>
                    <button className="temp"  onClick={this.handleInput('3')}>3</button>
                    <button className="temp"  onClick={this.handleInput('0')}>0</button>
                    <button className="temp"  onClick={this.getresult}>=</button>
                </div>


            </div>
        );
    }
}

export default Count;
