//加法的action创造者
export const createIncrementAction=(data)=> ({type:'increment',data})
//减法的action创造者
export const createDecrementAction=(data)=>({type:'decrement',data})
//乘法的action创造者
export const createMultiAction=(data)=>({type:'multi',data})
//除法的action创造者
export const createDiviAction=(data)=>({type:'divi',data})
