const initCount = 0;
export default function countReducer(preState=initCount,action){
    const{type,number} = action
    switch (type){
        case "increment":
            return preState + number;
        case "decrement":
            return preState - number;
        case "multi":
            return preState * number;
        case "divi":
            return preState / number;
        default:
            return preState;
    }
}
