import React, {Component} from 'react';
import './index.less'
class Count extends Component {
    //加
    addhandle= ()=>{
        const{value} = this.selectNumber;
        this.props.addnumber(value*1);
    }
    dechandle=()=>{
        const{value} = this.selectNumber;
        this.props.dec(value*1);
    }
    multihandle=()=>{
        const{value}=this.selectNumber;
        this.props.multi(value*1);
    }
    divihandle=()=>{
        const{value} = this.selectNumber;
        this.props.divi(value*1);
    }

    render() {
        return (
            <div className="Count">
                <div className="Count_head">
                    <h1>当前的和为:{this.props.count}</h1>
                </div>
                
                <select ref={(c)=>{this.selectNumber=c}}>
                    <option value='1'>1</option>
                    <option value='2'>2</option>
                    <option value='3'>3</option>
                </select>&nbsp;&nbsp;
                <button onClick={this.addhandle}>+</button>&nbsp;
                <button onClick={this.dechandle}>-</button>&nbsp;
                <button onClick={this.multihandle}>*</button>&nbsp;
                <button onClick={this.divihandle}>/</button>&nbsp;
            </div>
        );
    }
}

export default Count;
