import React, {Component} from 'react';
import './index.less'
class Count extends Component {
    //加
    addhandle= ()=>{
        const{value} = this.selectNumber;
        this.props.addnumber(value*1);
    }
    dechandle=()=>{
        const{value} = this.selectNumber;
        this.props.dec(value*1);
    }
    multihandle=()=>{
        const{value}=this.selectNumber;
        this.props.multi(value*1);
    }
    divihandle=()=>{
        const{value} = this.selectNumber;
        this.props.divi(value*1);
    }
    state={
        //记录你点击的数字的
        input_number:0
    }
    render() {
        return (
            <div className="Count">
                <div className="Count_head">
                    <h1>{this.props.count}{this.state.input_number}</h1>
                </div>
                <div className="Count_main">
                    <div className="Count_do">
                        <button className="temp" onClick={this.addhandle}>+</button>
                        <button className="temp" onClick={this.dechandle}>-</button>
                        <button className="temp" onClick={this.multihandle}>*</button>
                        <button className="temp" onClick={this.divihandle}>/</button>
                    </div>
                    <button className="temp" value="7">7</button>
                    <button className="temp" value="8">8</button>
                    <button className="temp" value="9">9</button>
                    <button className="temp" value="4">4</button>
                    <button className="temp" value="5">5</button>
                    <button className="temp" value="6">6</button>
                    <button className="temp" value="1">1</button>
                    <button className="temp" value="2">2</button>
                    <button className="temp" value="3">3</button>
                    <button className="temp" value="0">0</button>
                </div>


            </div>
        );
    }
}

export default Count;
